import {Component, Input} from '@angular/core';
import {OfferModel} from '../../core/marketplace-api/models/offer.model';

@Component({
  selector: 'app-offer-item',
  templateUrl: './offer-item.component.html',
  styleUrls: ['./offer-item.component.scss']
})
export class OfferItemComponent{

  @Input()
  offer: OfferModel;

  constructor() { }


}
<div class="card card-body col-12 col-md-6 col-lg-4">
  <h3 class="title card-title bg-dark text-white">{{ offer.title }}</h3>
  <div class="picture">
    <img [src]="offer.imgURL|concatBeforeIfNotURLOnline" alt="offer-image" />
  </div>
  <div class="byline">
    <span class="user">{{ offer.username }}</span>
    <span class="published-on">{{ offer.date| date:'short' }}</span>
  </div>
  <p class="description card-text">{{ offer.description }}</p>
  <div class="">
    <label>{{offer.category}}</label>
  </div>

</div>
