import { Component, Input, OnInit, Output, EventEmitter } from '@angular/core';
import { OffersModule } from '../../offers.module';
import { Page } from 'src/app/core/models/page.model';

@Component({
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.scss']
})
export class PaginationComponent implements OnInit {
  @Input() totalPages!:number;
  @Output() pageIndex = new EventEmitter<number>();
  index = 1
  page!:Page<OffersModule>;

  ngOnInit(){
    this.page = new Page([],this.index,this.totalPages)
  }

  updatePage(index:number){
    this.index = index;
    this.page.PageIndex = this.index
    this.pageIndex.next(index)

  }

  nextIndex(){
    if(this.page.nextIndex){
      this.index++
      this.page.PageIndex = this.index
      this.pageIndex.next(this.index)
    }
  }

  preIndex(){
    if(this.index >1){
      this.index--
      this.page.PageIndex = this.index
      this.pageIndex.next(this.index)
    }
  }
}
<div class="container">
  <div class="row">
    <div class="d-flex flex-row justify-content-center">

        <button [ngClass]="index==1?'d-none':'d-flex first fs'" (click)="preIndex()" class="btn btn-outline-primary"><<</button>
        <button (click)="updatePage(btn)"   class="btn btn-outline-primary" *ngFor="let btn of page.getPreviousPageIndexes();">{{btn}}</button>
        <button [ngClass]="{'first':page.getPreviousPageIndexes() === null, 'last':page.getNextPageIndexes() === null}"   (click)="updatePage(index)"  class="btn btn-primary" >{{index}}</button>
        <button (click)="updatePage(btn)" class="btn btn-outline-primary" *ngFor="let btn of page.getNextPageIndexes()">{{btn}}</button>
        <button [ngClass]="index==totalPages?'d-none':'d-flex last fs'"  (click)="nextIndex()" class="btn btn-outline-primary">>></button>
      </div>



  </div>
</div>
