import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.scss']
})
export class PaginationComponent implements OnInit {
  @Input() totalPages:number = 0;
  counter = Array
  index = 1

  ngOnInit(){
    console.log(this.totalPages)
  }

  updatePage(index:number){
    this.index = index;
  }

  nextIndex(){
    if(this.index <this.totalPages){
      this.index ++
    }
  }

  preIndex(){
    if(this.index >1){
      this.index --
    }
  }
}
<div class="container">
  <div class="row">
    <div class="d-flex flex-row justify-content-center">
      <button [ngClass]="index==1?'d-none':'d-flex'" (click)="preIndex()" class="btn btn-light"><<</button>
      <button (click)="updatePage(i+1)" [ngClass]="i+1===index?'btn-primary':'btn-light'" class="btn" *ngFor="let btn of counter(totalPages); let i = index">{{i+1}}</button>
      <button [ngClass]="index==totalPages?'d-none':'d-flex'"  (click)="nextIndex()" class="btn btn-light">>></button>
    </div>

  </div>
</div>
