import {Component, Input} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, UntypedFormGroup, Validators} from '@angular/forms';
import { Router } from '@angular/router';
import { MarketplaceApiService } from 'src/app/core/marketplace-api/marketplace-api.service';
import { Category, OfferModel } from 'src/app/core/marketplace-api/models/offer.model';

@Component({
  selector: 'app-offer-creation',
  templateUrl: './offer-creation.component.html',
  styleUrls: ['./offer-creation.component.scss']
})
export class OfferCreationComponent{

  offerForm: FormGroup = this.formBuilder.group<OfferModel>({
    title: '',
    imgURL:'',
    description:'',
    location:'',
    category: "I'm looking for"
  });

  @Input()
  categories: string[] = ["I'm looking for", "Product", "Service"];


  constructor(
    private formBuilder:FormBuilder,
    private marketplaceService:MarketplaceApiService,
    private router:Router
  ) {

  }

  offerSubmit() {
    const formValues:OfferModel = this.offerForm.getRawValue()
    this.marketplaceService.postOffer(formValues).subscribe({
      next: ()=> {
        formValues.date = new Date()
        console.log(formValues)
      }
      //this.router.navigate(['/offer/list'])
    })
  }
}
<form [formGroup]="offerForm" class="offer-form container form-floating mt-4">
  <div class="row justify-content-center">
    <div class="col-md-6 col-sm-8 card p-4">
      <h3>Post new Offer</h3>
      <div class="form-group mt-2">
        <label for="titlel" class="form-label fw-bold">Title</label>
        <input formControlName="title" id="title" class="form-control" placeholder="Title" />
      </div>
      <div class="form-group mt-2">
        <label for="picture-url" class="form-label fw-bold">Picture URL</label>
        <input
          formControlName="imgURL"
          class="form-control"
          id="picture-url"
          placeholder="offers/iphone.jpg"
        />
      </div>
      <div class="form-group mt-2 d-flex flex-column">
        <label for="description" class="form-label fw-bold">Description</label>
        <textarea formControlName="description"
          class="form-control"
          id="description"
          class=""
          placeholder="Description"
          rows="5"
        ></textarea>
      </div>
      <div class="form-group mt-2">
        <label for="location" class="form-label fw-bold m-2">Location</label>
        <input
          class="form-control"
          id="location"
          class=""
          formControlName="location"
          placeholder="Country, city, neighborhood, the office, my desk, etc..."
        />
      </div>
      <div class="form-group mt-2">
        <label for="category" class="form-label fw-bold m-2">Category</label>
        <select formControlName="category" class="form-select" id="category" class="">
          <option [value]="item" *ngFor="let item of categories">
            {{ item }}
          </option>
        </select>
      </div>
      <div class="row">
        <div class="col-6">
          <button
          (click)="offerSubmit()"
            type="submit"
            class="offer-submit-button btn btn-primary mt-4"
          >
            Create
          </button>
        </div>
      </div>
    </div>
  </div>
</form>
