import { Component, OnInit } from '@angular/core';
import { MarketplaceApiService } from 'src/app/core/marketplace-api/marketplace-api.service';
import { OfferModel } from 'src/app/core/marketplace-api/models/offer.model';

@Component({
  selector: 'app-offer-list',
  templateUrl: './offer-list.component.html',
  styleUrls: ['./offer-list.component.scss']
})
export class OfferListComponent implements OnInit {

  pageSize = 4;
  offers: OfferModel[]

  constructor(
    private marketplaceService:MarketplaceApiService
  ) { }

  ngOnInit(): void {

    this.marketplaceService.getOffers(3, this.pageSize).subscribe(offers=>{
      this.offers = offers
      console.log(this.offers)
    })
  }

}

<app-offer-item *ngFor="let offer of offers" (offer) = offer ></app-offer-item>
